import React, { useState } from 'react';
import { Box, Button, Stepper, StepperLabel, Text, useClipboard } from '@chakra-ui/react';

const GeradorDeSenha = () => {
  const [tamanhoSenha, setTamanhoSenha] = useState(8);
  const [senhaGerada, setSenhaGerada] = useState('');
  const [erro, setErro] = useState('');
  const { hasCopied, onCopy } = useClipboard(senhaGerada);

  const handleTamanhoSenhaChange = (value) => {
    setTamanhoSenha(value);
  };

  const gerarSenha = () => {
    if (tamanhoSenha < 6 || tamanhoSenha > 40) {
      setErro('O tamanho da senha deve ser entre 6 e 40 caracteres.');
      setSenhaGerada('');
      return;
    }

    const caracteres =
      'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()';
    let senha = '';

    for (let i = 0; i < tamanhoSenha; i++) {
      const indiceAleatorio = Math.floor(Math.random() * caracteres.length);
      senha += caracteres[indiceAleatorio];
    }

    setErro('');
    setSenhaGerada(senha);
  };

  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      height="100vh"
    >
      <Box width="400px">
        <Stepper defaultValue={tamanhoSenha} min={6} max={40} onChange={handleTamanhoSenhaChange}>
          <StepperLabel>Tamanho da senha: {tamanhoSenha}</StepperLabel>
        </Stepper>
        <Button colorScheme="blue" onClick={gerarSenha} mt={4}>
          Gerar Senha
        </Button>
        {erro && (
          <Text color="red" mt={4}>
            {erro}
          </Text>
        )}
        {senhaGerada && (
          <Box mt={4}>
            <Text fontWeight="bold">Senha Gerada:</Text>
            <Box display="flex" alignItems="center">
              <Text mr={2}>{senhaGerada}</Text>
              <Button onClick={onCopy}>{hasCopied ? 'Copiado' : 'Copiar'}</Button>
            </Box>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default GeradorDeSenha;
