import React from 'react';
import { Box, Button, NumberInput, Text, VStack, Center } from '@chakra-ui/react';
import gerarSenha from './funcaoGeraSenha';

function Gerador() {
  const [tamanho, setTamanho] = React.useState(6);
  const [senha, setSenha] = React.useState('');

  const handleGerarSenha = () => {
    const novaSenha = gerarSenha(tamanho);
    setSenha(novaSenha);
  };

  return (
    <Center h='100%'>
            <Box p={4}>
      <VStack spacing={4} align="stretch">
        <Text fontSize="xl" fontWeight="bold">Gerador de Senha</Text>
        <NumberInput
          placeholder="Tamanho da senha"
          min={6}
          max={40}
          value={tamanho}
          onChange={(e) => setTamanho(Number(e.target.value))}
        />
        <Button colorScheme="blue" onClick={handleGerarSenha}>Gerar Senha</Button>
        <Text fontSize="lg" fontWeight="bold">Senha Gerada:</Text>
        <Text fontSize="xl">{senha}</Text>
      </VStack>
    </Box>
    </Center>

  );
}

export default Gerador;
