import React, { useState } from 'react';
import { Box, Button, Input, Text, useClipboard } from '@chakra-ui/react';

const GeradorDeSenha = () => {
  const [tamanhoSenha, setTamanhoSenha] = useState('');
  const [senhaGerada, setSenhaGerada] = useState('');
  const { hasCopied, onCopy } = useClipboard(senhaGerada);

  const handleTamanhoSenhaChange = (event) => {
    setTamanhoSenha(event.target.value);
  };

  const gerarSenha = () => {
    if (tamanhoSenha < 6 || tamanhoSenha > 40) {
      return <div>'O tamanho da senha deve ser entre 6 e 40 caracteres.'</div>

    }

    const caracteres =
      'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#';
    let senha = '';

    for (let i = 0; i < tamanhoSenha; i++) {
      const indiceAleatorio = Math.floor(Math.random() * caracteres.length);
      senha += caracteres[indiceAleatorio];
    }

    setSenhaGerada(senha);
  };

  return (
    <Box>
      <Input
        type="number"
        placeholder="Tamanho da senha"
        value={tamanhoSenha}
        onChange={handleTamanhoSenhaChange}
      />
      <Button colorScheme="blue" onClick={gerarSenha}>
        Gerar Senha
      </Button>
      {senhaGerada && (
        <Box mt={4}>
          <Text fontWeight="bold">Senha Gerada:</Text>
          <Box display="flex" alignItems="center">
            <Text mr={2}>{senhaGerada}</Text>
            <Button onClick={onCopy}>{hasCopied ? 'Copiado' : 'Copiar'}</Button>
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default GeradorDeSenha;
