import React from "react";
import {
  Box,
  Button,
  NumberInput,
  Text,
  VStack,
  Center,
  NumberInputField,
  NumberInputStepper,
  NumberDecrementStepper,
  NumberIncrementStepper,
} from "@chakra-ui/react";

import gerarSenha from "./funcaoGeraSenha";

function Gerador() {
  const [tamanho, setTamanho] = React.useState(6);
  const [senha, setSenha] = React.useState("");

  const handleTamanhoChange = (value) => {
    setTamanho(value);
  };

  const handleGerarSenha = () => {
    const novaSenha = gerarSenha(tamanho);
    setSenha(novaSenha);
  };

  return (
    <Center h="100%">
      <Box borderWidth={1} width={400} borderRadius={10} p={4}>
        <VStack spacing={4} align="stretch">
            <Center flexDirection={'column'}> <Text fontSize="xl" fontWeight="bold">
            Gerador de Senha
          </Text>

          <NumberInput
          maxW={40}
            placeholder="Tamanho da senha"
            min={6}
            max={40}
            value={tamanho}
            onChange={handleTamanhoChange}
          >
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
          <Button colorScheme="blue" onClick={handleGerarSenha}>
            Gerar Senha
          </Button>
          </Center>
          <Text fontSize="md" fontWeight="bold">
            Senha Gerada:
          </Text>
          <Text fontSize={senha.length >= 33 ? "sm" : "xl"}>{senha}</Text>
        </VStack>
      </Box>
    </Center>
  );
}

export default Gerador;
