import React from "react";
import {
  Box,
  Button,
  NumberInput,
  Text,
  VStack,
  Center,
  NumberInputField,
  NumberInputStepper,
  NumberDecrementStepper,
  NumberIncrementStepper,
  Fade,
  InputGroup,
  InputLeftAddonButton,
  InputRightAddonButton,
  Flex
} from "@chakra-ui/react";

import gerarSenha from "./funcaoGeraSenha";

function Gerador() {
  const [tamanho, setTamanho] = React.useState(6);
  const [senha, setSenha] = React.useState("");
  const [senhaGerada, setSenhaGerada] = React.useState(false); // Estado para controlar se a senha foi gerada

  const handleTamanhoChange = (value) => {
    setTamanho(value);
  };

  const handleGerarSenha = () => {
    const novaSenha = gerarSenha(tamanho);
    setSenha(novaSenha);
    setSenhaGerada(true); // Define o estado de senha gerada como true
  };

  return (
    <Center h="100vh">
      <Box borderWidth={1} width={445} borderRadius={10} p={4}>
        <VStack spacing={4} align="stretch">
          <Center p={1} flexDirection={"column"}>
            <Text fontSize="xl" fontWeight="bold">
              Gerador de Senha
            </Text>
            <Text fontSize={"sm"} mb={3}>
              tamanho da senha:
            </Text>
            <NumberInput
              maxW={40}
              placeholder="Tamanho da senha"
              min={6}
              max={40}
              value={tamanho}
              onChange={handleTamanhoChange}
              mb={4}
            >
              <NumberInputField />
              <NumberInputStepper>
                <NumberIncrementStepper />
                <NumberDecrementStepper />
              </NumberInputStepper>
            </NumberInput>
            <Button colorScheme="blue" onClick={handleGerarSenha}>
              Gerar Senha
            </Button>
          </Center>
          {senhaGerada && (
            <Fade in>
              <Box>
                <Box
                  borderRadius="md"
                  height={50}
                  maxHeight={50}
                  borderWidth={1}
                  p={2}
                >
                 <Flex>
                    <Box bg={'gray.200'} p={2} border={'1px solid gray'} mb={1}flexDirection={'column'} mr={5}>Senha:</Box>
                  <Center>
                    <Text fontSize={senha.length >= 33 ? "sm" : "xl"} color={"gray.700"}>
                      {senha}
                    </Text>
                  </Center>
                  </Flex>
                </Box>
              </Box>
            </Fade>
          )}
        </VStack>
      </Box>
    </Center>
  );
}

export default Gerador;
